// This file contains base64-encoded audio placeholders for the game
// These can be used until you add real audio files

// Empty audio file (1ms silent MP3)
const emptyAudioBase64 = 'data:audio/mp3;base64,';

// Export the audio placeholders
window.audioPlaceholders = {
  bounce: emptyAudioBase64,
  break: emptyAudioBase64,
  powerup: emptyAudioBase64
};

// Add these to the document so they can be accessed by the game
document.addEventListener('DOMContentLoaded', () => {
  // Create a hidden div to store audio elements
  const audioContainer = document.createElement('div');
  audioContainer.style.display = 'none';
  document.body.appendChild(audioContainer);
  
  // Create audio elements for each sound
  const sounds = ['bounce', 'break', 'powerup'];
  sounds.forEach(sound => {
    const audio = document.createElement('audio');
    audio.id = `audio-${sound}`;
    audio.src = window.audioPlaceholders[sound];
    audioContainer.appendChild(audio);
  });
});